export function localizeMessage(runtimeCode: number, language: string, region: string): string {
	let localizations: ReadonlyArray<{ readonly runtimeCode: number, readonly vocabTokens: ReadonlyArray<number> }> = MESSAGE_STRINGS.find(ms => ms.language === DEFAULT_PROPERTY_VALUE && ms.region === DEFAULT_PROPERTY_VALUE).localizations;

	const languageLocalizationSets = MESSAGE_STRINGS.filter(ms => ms.language === language);
	const regionLocalizationSet = languageLocalizationSets.find(ls => ls.region === region);

	if (!!regionLocalizationSet) {
		localizations = regionLocalizationSet.localizations;
	} else {
		const languageDefault = MESSAGE_STRINGS.find(ms => ms.language === language && ms.region === DEFAULT_PROPERTY_VALUE);
		if (!!languageDefault) {
			localizations = languageDefault.localizations;
		}
	}

	const localization = localizations.find(lzn => lzn.runtimeCode === runtimeCode);
	if (!!localization) {
		return localization.vocabTokens
			.map(ti => TOKENS[ti])
			.reduce((x, y, i, a) => `${x}${y}` + (i === a.length - 1 ? '' : ' '), '');
	} else {
		return '?';
	}
}
